import React, { useState } from 'react';
import './search.css';

export default class Search extends
React.Component{
    constructor(props){
        super(props);
        this.props = props;
        this.state = {
            message: localStorage.getItem("message") || ""
        };
    }
    onChange = event => {
        const message = event.target.value;
    
        localStorage.setItem(this.props.json, message);
        this.setState({ message });
      };
    render(){
        return <div class="search-container">
            <form action="/action_page.php">
            <input type="text" value={this.state.message} placeholder= {this.props.title} onChange={this.onChange} name="search"/>
            </form>
        </div>;
    }
}
